import genai from 'genai'; // Importa a biblioteca genai

// Inicializa o cliente da API GenAI
const client = new genai.Client({
  apiKey: 'AIzaSyDc57MueVt9XPC00RI4ycHN64hviz58v5g', // Substitua pela sua chave de API
});

// Função para enviar mensagens ao modelo Gemini
export const sendMessageToGemini = async (message: string) => {
  try {
    // Faz a chamada ao modelo Gemini
    const response = await client.models.generate_content({
      model: 'gemini-2.0-flash', // Modelo a ser usado
      contents: message, // Mensagem enviada ao modelo
    });

    // Retorna o texto gerado pelo modelo
    return response.text;
  } catch (error) {
    console.error('Erro ao se comunicar com a API GenAI:', error);
    throw error;
  }
};
