import axios from 'axios';
const geminiApi = axios.create({
  baseURL: 'https://generativelanguage.googleapis.com/v1beta2/models/chat-bison-001:generateMessage',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
    'X-Api-Key': 'AIzaSyDc57MueVt9XPC00RI4ycHN64hviz58v5g',
  },
});

geminiApi.interceptors.request.use((config) => {
  const token = 'AIzaSyDc57MueVt9XPC00RI4ycHN64hviz58v5g'; // Substitua pelo token de autenticação
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export const sendMessageToGemini = async (message: string) => {
  try {
    const response = await geminiApi.post('', {
      prompt: {
        messages: [
          {
            author: 'user',
            content: message,
          },
        ],
      },
    });
    return response.data;
  } catch (error) {
    console.error('Erro ao enviar mensagem para o Gemini:', error.response?.data || error.message);
    throw error;
  }
};

export default geminiApi;
